INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (1, '67fb7221e6b2a', '67eb06e072bfa', '<p>新中国成年是哪一年</p>', '[{\"check\": \"A\", \"title\": \"<p>1949</p>\", \"is_check\": \"\"}, {\"check\": \"B\", \"title\": \"<p>1950</p>\", \"is_check\": \"\"}]', 1.00, '[\"A\"]', '<p><br></p>', 1, 0, 1, 1744532001, 1744532001, NULL, 1, 1, 1, '67fa2a7fe3b13');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (2, '67fb798c1f103', '67eb06e072bfa', '<p>下面的试题内容</p>', '[{\"check\": \"A\", \"title\": \"<p>请输入选项值1</p>\", \"is_check\": \"\"}, {\"check\": \"B\", \"title\": \"<p>请输入选项值2</p>\", \"is_check\": \"\"}, {\"check\": \"C\", \"title\": \"<p>请输入选项值</p>\", \"is_check\": \"\"}]', 1.00, '[\"A\", \"B\"]', '', 2, 0, 1, 1744533900, 1744533900, NULL, 1, 1, 1, '67fa2a7fe3b13');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (3, '681642fc27a9f', '68163f929613d', '<p>根据《建筑法》，下列属于建筑工程施工许可必备条件的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"施工单位已缴纳农民工工资保证金\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"建设资金已落实且不少于工程合同价的30%\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"拆迁工作全部完成\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工图纸经建设单位审核通过\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>《建筑法》规定，申请施工许可证需满足建设资金已落实（工期不足1年的到位资金不少于50%，超过1年的不少于30%），其他选项非法定必备条件。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (4, '681642fc27abe', '68163f929613d', '<p>建筑工程施工中，混凝土灌注桩的桩身完整性检测应采用（ ）</p>', '[{\"check\": \"A\", \"title\": \"回弹法\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"钻芯法\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"低应变法\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"超声回弹综合法\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>低应变法主要用于检测桩身完整性，钻芯法用于检测混凝土强度，回弹法和超声回弹综合法用于结构构件强度检测。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (5, '681642fc27ac5', '68163f929613d', '<p>下列属于危险性较大的分部分项工程的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"层高3m的模板支撑工程\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"开挖深度4m的基坑支护工程\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"建筑面积1万㎡的脚手架工程\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"高度20m的落地式钢管脚手架工程\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>开挖深度≥3m的基坑支护工程属于危险性较大工程，需编制专项施工方案；深度≥5m为超过一定规模的危险性工程。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (6, '681642fc27acb', '68163f929613d', '<p>建筑工程竣工验收的主体是（ ）</p>', '[{\"check\": \"A\", \"title\": \"建设单位\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"施工单位\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"监理单位\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"质量监督机构\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>建设单位负责组织勘察、设计、施工、监理等单位进行竣工验收，是验收主体。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (7, '681642fc27ad0', '68163f929613d', '<p>根据《建设工程质量管理条例》，屋面防水工程的最低保修期限为（ ）</p>', '[{\"check\": \"A\", \"title\": \"2年\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"5年\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"10年\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"设计文件规定的合理使用年限\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>屋面防水、卫生间防水和外墙面防渗漏的最低保修期为5年，其他项目按条例规定执行。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (8, '681642fc27ad5', '68163f929613d', '<p>钢筋混凝土结构中，钢筋的连接方式不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"绑扎连接\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"机械连接\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"电渣压力焊连接\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"套管灌浆连接\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>套管灌浆连接是装配式混凝土结构中预制构件的连接方式，不属于钢筋连接方式。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (9, '681642fc27ada', '68163f929613d', '<p>建筑工程施工组织设计的核心内容是（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程概况\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"施工方案\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"施工进度计划\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"资源配置计划\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>施工方案是施工组织设计的核心，决定了施工方法、工艺和技术措施，直接影响工程质量和效率。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (10, '681642fc27adf', '68163f929613d', '<p>下列属于建筑工程规费的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"安全文明施工费\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"工程排污费\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"总承包服务费\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"计日工费用\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>规费包括社会保险费、住房公积金和工程排污费（已并入环境保护税，此处按传统考点设置），其他选项属于措施费或其他项目费。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (11, '681642fc27aed', '68163f929613d', '<p>混凝土强度等级C30中的“30”表示（ ）</p>', '[{\"check\": \"A\", \"title\": \"立方体抗压强度标准值30MPa\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"轴心抗压强度设计值30MPa\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"立方体抗压强度设计值30MPa\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"轴心抗压强度标准值30MPa\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>混凝土强度等级按立方体抗压强度标准值划分，C30即标准值≥30MPa且＜35MPa。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (12, '681642fc27af5', '68163f929613d', '<p>根据《建设工程安全生产管理条例》，施工单位的安全责任不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"制定安全生产规章制度\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"为作业人员缴纳意外伤害保险\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"对达到一定规模的危险性工程组织专家论证\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"对因建设工程施工可能造成损害的毗邻建筑物进行防护\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>意外伤害保险已改为非强制险（工伤保险为强制），其他选项均为施工单位法定安全责任。</p>', 1, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (13, '681642fc27b00', '68163f929613d', '<p>下列属于建筑工程竣工验收应当具备的条件有（ ）</p>', '[{\"check\": \"A\", \"title\": \"完成建设工程设计和合同约定的各项内容\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"有完整的技术档案和施工管理资料\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"有工程使用的主要建筑材料、构配件和设备的进场试验报告\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"有施工单位签署的工程保修书\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>竣工验收条件不包括工程款支付凭证，需满足质量相关文件齐全、完成合同内容等。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (14, '681642fc27b0a', '68163f929613d', '<p>下列属于建筑工程施工质量验收层次的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"单位工程\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"分部工程\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"分项工程\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"检验批\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工质量验收层次包括单位工程、分部工程、分项工程和检验批，工序不属于验收层次。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (15, '681642fc27b0e', '68163f929613d', '<p>根据《招标投标法》，下列情形中，投标文件无效的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"投标文件未按招标文件要求密封\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"投标人未按时提交投标保证金\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"投标报价低于成本价\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"投标文件未加盖投标人公章但有法定代表人签字\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>投标文件需密封、提交保证金、报价合理、主体一致，D选项中“未加盖公章但有法定代表人签字”通常视为无效（需同时盖章和签字）。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (16, '681642fc27b16', '68163f929613d', '<p>下列属于建筑工程施工安全检查内容的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"安全规章制度执行情况\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"劳动防护用品使用情况\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"基坑支护变形监测数据\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工临时用电合规性\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>安全检查侧重安全管理、设施、用电、防护等，混凝土试块强度属于质量检查内容。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (17, '681642fc27b1c', '68163f929613d', '<p>下列关于钢筋混凝土结构施工缝留置原则的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"宜留置在结构受剪力较小且便于施工的部位\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"柱的施工缝可留置在基础顶面或梁底面\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"单向板的施工缝可留置在平行于板短边的任何位置\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"有主次梁的楼板，施工缝宜留置在次梁跨度的中间1/3范围内\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\", \"E\"]', '<p>单向板施工缝应留置在平行于板短边的任何位置，C选项正确；其他选项均符合施工缝留置规范。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (18, '681642fc27b25', '68163f929613d', '<p>下列属于装配式建筑优点的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"提高施工效率，缩短工期\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"减少施工现场污染\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"提高建筑整体抗震性能\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"降低工程造价\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>装配式建筑初期造价可能高于传统建筑，但长期维护成本低，D选项错误。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (19, '681642fc27b2d', '68163f929613d', '<p>下列属于建筑工程施工图审查内容的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"是否符合工程建设强制性标准\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"地基基础和主体结构的安全性\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"消防设计是否符合规范\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工组织设计是否合理\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>施工图审查不涉及施工组织设计，重点审查安全性、强制性标准、消防和节能等。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (20, '681642fc27b38', '68163f929613d', '<p>下列关于建筑工程脚手架搭设要求的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"立杆基础应平整夯实，设置排水措施\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"双排脚手架横向水平杆靠墙一端至墙装饰面的距离不应大于100mm\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"脚手板应满铺，不得有空隙和探头板\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"高度超过24m的落地式脚手架应采用刚性连墙件\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>剪刀撑应从底部到顶部连续设置，E选项“从顶部开始”错误，应从底部开始。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (21, '681642fc27b43', '68163f929613d', '<p>下列属于建筑工程质量事故处理依据的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"质量事故调查报告\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"有关的设计文件和施工资料\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"工程建设强制性标准\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"类似工程质量事故处理案例\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>处理依据包括事实资料、合同文件、技术标准等，类似案例可参考但非法定依据。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (22, '681642fc27b4e', '68163f929613d', '<p>下列属于建筑工程进度控制措施的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"建立进度管理责任制度\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"优化施工方案，采用先进技术\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"及时支付工程进度款\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"建立进度动态监测机制\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>进度控制措施包括组织、技术、经济、管理措施，罚款属于合同违约处理，非直接进度控制措施。</p>', 2, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (23, '681642fc27b56', '68163f929613d', '<p>建筑工程施工许可证有效期为3个月，可延期2次，每次不超过3个月。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建筑法》规定，施工许可证自发证之日起3个月内开工，可延期两次，每次3个月。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (24, '681642fc27b59', '68163f929613d', '<p>建设单位可以将建筑工程肢解后分别发包给多个施工单位。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>肢解发包属于违法行为，建设单位应将工程发包给具有相应资质的单位。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (25, '681642fc27b5c', '68163f929613d', '<p>混凝土浇筑完成后，应在12小时内覆盖并洒水养护，养护时间不少于7天。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>普通混凝土养护时间≥7天，防水混凝土≥14天，题干未明确防水混凝土，表述正确。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (26, '681642fc27b69', '68163f929613d', '<p>建筑工程施工中，项目经理必须持有注册建筑师资格证书。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>项目经理需持有注册建造师资格证书，注册建筑师主要从事设计工作。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (27, '681642fc27b6d', '68163f929613d', '<p>危险性较大的分部分项工程专项施工方案必须经施工单位技术负责人和总监理工程师签字后实施。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>超过一定规模的危险性工程还需专家论证，但普通危险性工程仅需上述签字。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (28, '681642fc27b72', '68163f929613d', '<p>建筑工程竣工验收合格后，建设单位应当在15日内将竣工验收报告报建设行政主管部门备案。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建设工程质量管理条例》规定，备案期限为验收合格后15日内。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (29, '681642fc27b74', '68163f929613d', '<p>钢筋保护层厚度不足会导致混凝土结构承载力下降，但不影响耐久性。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>保护层不足会导致钢筋锈蚀，严重影响结构耐久性。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (30, '681642fc27b7a', '68163f929613d', '<p>施工组织设计中的施工平面图应标明临时设施、材料堆放、机械布置等位置。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>施工平面图是施工组织设计的重要组成部分，需规划现场布局。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (31, '681642fc27b7c', '68163f929613d', '<p>建筑工程质量保修期限应不低于国家规定的最低标准，但可以约定更长的保修期限。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>法律法规规定最低保修期限，发承包双方可约定更高标准。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (32, '681642fc27b80', '68163f929613d', '<p>建设工程招标中，投标人报价明显低于成本价的，评标委员会应当直接否决其投标。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>根据《招标投标法》，低于成本价的投标视为无效，应否决。</p>', 3, 1, 1, 1746289404, 1746289404, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (33, '681643e17aa14', '68163f929613d', '<p>根据《建筑法》，下列属于建筑工程施工许可必备条件的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"施工单位已缴纳农民工工资保证金\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"建设资金已落实且不少于工程合同价的30%\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"拆迁工作全部完成\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工图纸经建设单位审核通过\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>《建筑法》规定，申请施工许可证需满足建设资金已落实（工期不足1年的到位资金不少于50%，超过1年的不少于30%），其他选项非法定必备条件。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (34, '681643e17dbb8', '68163f929613d', '<p>建筑工程施工中，混凝土灌注桩的桩身完整性检测应采用（ ）</p>', '[{\"check\": \"A\", \"title\": \"回弹法\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"钻芯法\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"低应变法\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"超声回弹综合法\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>低应变法主要用于检测桩身完整性，钻芯法用于检测混凝土强度，回弹法和超声回弹综合法用于结构构件强度检测。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (35, '681643e17eb9d', '68163f929613d', '<p>下列属于危险性较大的分部分项工程的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"层高3m的模板支撑工程\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"开挖深度4m的基坑支护工程\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"建筑面积1万㎡的脚手架工程\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"高度20m的落地式钢管脚手架工程\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>开挖深度≥3m的基坑支护工程属于危险性较大工程，需编制专项施工方案；深度≥5m为超过一定规模的危险性工程。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (36, '681643e17ebc1', '68163f929613d', '<p>建筑工程竣工验收的主体是（ ）</p>', '[{\"check\": \"A\", \"title\": \"建设单位\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"施工单位\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"监理单位\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"质量监督机构\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>建设单位负责组织勘察、设计、施工、监理等单位进行竣工验收，是验收主体。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (37, '681643e17ebd3', '68163f929613d', '<p>根据《建设工程质量管理条例》，屋面防水工程的最低保修期限为（ ）</p>', '[{\"check\": \"A\", \"title\": \"2年\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"5年\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"10年\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"设计文件规定的合理使用年限\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>屋面防水、卫生间防水和外墙面防渗漏的最低保修期为5年，其他项目按条例规定执行。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (38, '681643e17ebd9', '68163f929613d', '<p>钢筋混凝土结构中，钢筋的连接方式不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"绑扎连接\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"机械连接\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"电渣压力焊连接\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"套管灌浆连接\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>套管灌浆连接是装配式混凝土结构中预制构件的连接方式，不属于钢筋连接方式。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (39, '681643e17ebe3', '68163f929613d', '<p>建筑工程施工组织设计的核心内容是（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程概况\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"施工方案\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"施工进度计划\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"资源配置计划\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>施工方案是施工组织设计的核心，决定了施工方法、工艺和技术措施，直接影响工程质量和效率。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (40, '681643e17ebe9', '68163f929613d', '<p>下列属于建筑工程规费的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"安全文明施工费\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"工程排污费\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"总承包服务费\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"计日工费用\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>规费包括社会保险费、住房公积金和工程排污费（已并入环境保护税，此处按传统考点设置），其他选项属于措施费或其他项目费。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (41, '681643e17ebf3', '68163f929613d', '<p>混凝土强度等级C30中的“30”表示（ ）</p>', '[{\"check\": \"A\", \"title\": \"立方体抗压强度标准值30MPa\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"轴心抗压强度设计值30MPa\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"立方体抗压强度设计值30MPa\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"轴心抗压强度标准值30MPa\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>混凝土强度等级按立方体抗压强度标准值划分，C30即标准值≥30MPa且＜35MPa。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (42, '681643e17ebfe', '68163f929613d', '<p>根据《建设工程安全生产管理条例》，施工单位的安全责任不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"制定安全生产规章制度\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"为作业人员缴纳意外伤害保险\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"对达到一定规模的危险性工程组织专家论证\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"对因建设工程施工可能造成损害的毗邻建筑物进行防护\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>意外伤害保险已改为非强制险（工伤保险为强制），其他选项均为施工单位法定安全责任。</p>', 1, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (43, '681643e17ec0d', '68163f929613d', '<p>下列属于建筑工程竣工验收应当具备的条件有（ ）</p>', '[{\"check\": \"A\", \"title\": \"完成建设工程设计和合同约定的各项内容\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"有完整的技术档案和施工管理资料\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"有工程使用的主要建筑材料、构配件和设备的进场试验报告\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"有施工单位签署的工程保修书\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>竣工验收条件不包括工程款支付凭证，需满足质量相关文件齐全、完成合同内容等。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (44, '681643e17ec19', '68163f929613d', '<p>下列属于建筑工程施工质量验收层次的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"单位工程\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"分部工程\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"分项工程\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"检验批\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工质量验收层次包括单位工程、分部工程、分项工程和检验批，工序不属于验收层次。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (45, '681643e17ec1e', '68163f929613d', '<p>根据《招标投标法》，下列情形中，投标文件无效的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"投标文件未按招标文件要求密封\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"投标人未按时提交投标保证金\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"投标报价低于成本价\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"投标文件未加盖投标人公章但有法定代表人签字\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>投标文件需密封、提交保证金、报价合理、主体一致，D选项中“未加盖公章但有法定代表人签字”通常视为无效（需同时盖章和签字）。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (46, '681643e17ec68', '68163f929613d', '<p>下列属于建筑工程施工安全检查内容的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"安全规章制度执行情况\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"劳动防护用品使用情况\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"基坑支护变形监测数据\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工临时用电合规性\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>安全检查侧重安全管理、设施、用电、防护等，混凝土试块强度属于质量检查内容。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (47, '681643e17ed3a', '68163f929613d', '<p>下列关于钢筋混凝土结构施工缝留置原则的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"宜留置在结构受剪力较小且便于施工的部位\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"柱的施工缝可留置在基础顶面或梁底面\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"单向板的施工缝可留置在平行于板短边的任何位置\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"有主次梁的楼板，施工缝宜留置在次梁跨度的中间1/3范围内\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\", \"E\"]', '<p>单向板施工缝应留置在平行于板短边的任何位置，C选项正确；其他选项均符合施工缝留置规范。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (48, '681643e17ed46', '68163f929613d', '<p>下列属于装配式建筑优点的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"提高施工效率，缩短工期\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"减少施工现场污染\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"提高建筑整体抗震性能\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"降低工程造价\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>装配式建筑初期造价可能高于传统建筑，但长期维护成本低，D选项错误。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (49, '681643e17ed53', '68163f929613d', '<p>下列属于建筑工程施工图审查内容的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"是否符合工程建设强制性标准\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"地基基础和主体结构的安全性\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"消防设计是否符合规范\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工组织设计是否合理\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>施工图审查不涉及施工组织设计，重点审查安全性、强制性标准、消防和节能等。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (50, '681643e17ed5e', '68163f929613d', '<p>下列关于建筑工程脚手架搭设要求的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"立杆基础应平整夯实，设置排水措施\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"双排脚手架横向水平杆靠墙一端至墙装饰面的距离不应大于100mm\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"脚手板应满铺，不得有空隙和探头板\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"高度超过24m的落地式脚手架应采用刚性连墙件\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>剪刀撑应从底部到顶部连续设置，E选项“从顶部开始”错误，应从底部开始。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (51, '681643e17ed6e', '68163f929613d', '<p>下列属于建筑工程质量事故处理依据的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"质量事故调查报告\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"有关的设计文件和施工资料\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"工程建设强制性标准\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"类似工程质量事故处理案例\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>处理依据包括事实资料、合同文件、技术标准等，类似案例可参考但非法定依据。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (52, '681643e17ed7b', '68163f929613d', '<p>下列属于建筑工程进度控制措施的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"建立进度管理责任制度\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"优化施工方案，采用先进技术\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"及时支付工程进度款\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"建立进度动态监测机制\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>进度控制措施包括组织、技术、经济、管理措施，罚款属于合同违约处理，非直接进度控制措施。</p>', 2, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (53, '681643e17ed85', '68163f929613d', '<p>建筑工程施工许可证有效期为3个月，可延期2次，每次不超过3个月。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建筑法》规定，施工许可证自发证之日起3个月内开工，可延期两次，每次3个月。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (54, '681643e17ed89', '68163f929613d', '<p>建设单位可以将建筑工程肢解后分别发包给多个施工单位。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>肢解发包属于违法行为，建设单位应将工程发包给具有相应资质的单位。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (55, '681643e17ed8d', '68163f929613d', '<p>混凝土浇筑完成后，应在12小时内覆盖并洒水养护，养护时间不少于7天。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>普通混凝土养护时间≥7天，防水混凝土≥14天，题干未明确防水混凝土，表述正确。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (56, '681643e17ef72', '68163f929613d', '<p>建筑工程施工中，项目经理必须持有注册建筑师资格证书。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>项目经理需持有注册建造师资格证书，注册建筑师主要从事设计工作。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (57, '681643e17ef83', '68163f929613d', '<p>危险性较大的分部分项工程专项施工方案必须经施工单位技术负责人和总监理工程师签字后实施。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>超过一定规模的危险性工程还需专家论证，但普通危险性工程仅需上述签字。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (58, '681643e17efb0', '68163f929613d', '<p>建筑工程竣工验收合格后，建设单位应当在15日内将竣工验收报告报建设行政主管部门备案。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建设工程质量管理条例》规定，备案期限为验收合格后15日内。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (59, '681643e17efb7', '68163f929613d', '<p>钢筋保护层厚度不足会导致混凝土结构承载力下降，但不影响耐久性。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>保护层不足会导致钢筋锈蚀，严重影响结构耐久性。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (60, '681643e17efc0', '68163f929613d', '<p>施工组织设计中的施工平面图应标明临时设施、材料堆放、机械布置等位置。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>施工平面图是施工组织设计的重要组成部分，需规划现场布局。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (61, '681643e17efc8', '68163f929613d', '<p>建筑工程质量保修期限应不低于国家规定的最低标准，但可以约定更长的保修期限。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>法律法规规定最低保修期限，发承包双方可约定更高标准。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (62, '681643e17efd0', '68163f929613d', '<p>建设工程招标中，投标人报价明显低于成本价的，评标委员会应当直接否决其投标。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>根据《招标投标法》，低于成本价的投标视为无效，应否决。</p>', 3, 1, 1, 1746289633, 1746289633, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (63, '6816447d31a81', '68163f929613d', '<p>根据《建筑法》，申请建筑工程施工许可证时，建设资金落实标准为工期超过1年的，到位资金不得少于工程合同价的（ ）</p>', '[{\"check\": \"A\", \"title\": \"20%\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"30%\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"40%\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"50%\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>《建筑法》规定，工期超过1年的建设工程，施工许可要求到位资金不少于合同价的30%。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (64, '6816447d31a98', '68163f929613d', '<p>下列钢筋连接方式中，属于机械连接的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"绑扎连接\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"电渣压力焊\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"套筒挤压连接\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"闪光对焊\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>机械连接包括套筒挤压、直螺纹套筒等，电渣压力焊和闪光对焊属于焊接连接。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (65, '6816447d31aa0', '68163f929613d', '<p>建筑工程施工中，用于检测混凝土抗压强度的标准试件尺寸是（ ）</p>', '[{\"check\": \"A\", \"title\": \"100mm×100mm×100mm\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"150mm×150mm×150mm\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"200mm×200mm×200mm\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"150mm×150mm×300mm\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>混凝土抗压强度标准试件为150mm立方体，符合《普通混凝土力学性能试验方法标准》。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (66, '6816447d31aed', '68163f929613d', '<p>根据《危险性较大的分部分项工程安全管理规定》，开挖深度超过（ ）的基坑支护工程需组织专家论证</p>', '[{\"check\": \"A\", \"title\": \"3m\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"4m\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"5m\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"6m\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>超过一定规模的危险性工程，开挖深度≥5m的基坑需专家论证。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (67, '6816447d31af3', '68163f929613d', '<p>建筑工程竣工验收时，单位工程质量验收合格的条件不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"所含分部工程质量均验收合格\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"质量控制资料完整\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"观感质量验收符合要求\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工单位已签署工程保修书\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>施工单位签署保修书是竣工验收应具备的条件，非单位工程质量验收合格的直接条件。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (68, '6816447d31b4a', '68163f929613d', '<p>下列属于建筑工程措施费的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程排污费\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"脚手架工程费\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"工伤保险费\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"总承包服务费\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>措施费包括脚手架、模板、安全文明施工等，工程排污费和工伤保险费属于规费，总承包服务费属于其他项目费。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (69, '6816447d31b5b', '68163f929613d', '<p>钢筋混凝土梁中，箍筋的主要作用是（ ）</p>', '[{\"check\": \"A\", \"title\": \"承受拉力\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"承受压力\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"抗剪和固定纵筋位置\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"提高混凝土强度\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>箍筋主要抵抗剪力，同时固定纵向钢筋位置，形成钢筋骨架。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (70, '6816447d31b7c', '68163f929613d', '<p>根据《建设工程安全生产管理条例》，施工单位应当为（ ）办理工伤保险</p>', '[{\"check\": \"A\", \"title\": \"全体职工\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"特种作业人员\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"现场管理人员\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"危险岗位作业人员\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>工伤保险是法定强制险，施工单位需为全体职工缴纳，意外伤害保险为自愿投保。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (71, '6816447d31b82', '68163f929613d', '<p>建筑工程施工组织设计中，施工进度计划的编制依据不包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"合同工期\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"施工方案\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"资源供应情况\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"工程地质勘察报告\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>工程地质勘察报告是施工方案编制依据，进度计划主要依据工期、资源、工艺等。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (72, '6816447d31b89', '68163f929613d', '<p>下列关于混凝土养护的说法，正确的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"高温天气下应在浇筑后立即覆盖养护\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"防水混凝土养护时间不得少于14天\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"日均温低于5℃时应采用洒水养护\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"养护用水的pH值不得小于7\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>防水混凝土因水泥水化需求，养护时间≥14天，低温时禁止洒水养护。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (73, '6816447d31b91', '68163f929613d', '<p>建筑工程中，下列材料需要进行见证取样检测的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"钢筋保护层垫块\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"墙体保温材料\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"砌筑砂浆试块\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"混凝土模板\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>见证取样范围包括水泥、钢筋、砂浆试块、混凝土试块等涉及结构安全的试块、试件。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (74, '6816447d31b9c', '68163f929613d', '<p>根据《招标投标法》，招标人对已发出的招标文件进行澄清，应在投标截止时间至少（ ）前书面通知投标人</p>', '[{\"check\": \"A\", \"title\": \"5日\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"10日\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"15日\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"20日\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>招标文件澄清需在投标截止前15天发出，否则需顺延截止时间。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (75, '6816447d31ba1', '68163f929613d', '<p>下列属于装配式混凝土结构主要连接方式的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"螺栓连接\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"套筒灌浆连接\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"焊接连接\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"铆钉连接\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>装配式混凝土结构中，预制构件主要通过套筒灌浆连接实现钢筋贯通。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (76, '6816447d31ba6', '68163f929613d', '<p>建筑工程施工质量验收中，检验批验收的组织者是（ ）</p>', '[{\"check\": \"A\", \"title\": \"建设单位项目负责人\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"施工单位项目负责人\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"监理工程师\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"质量监督机构人员\", \"is_check\": false}]', 1.00, '[\"C\"]', '<p>检验批由监理工程师组织施工单位项目专业质量检查员、专业工长等进行验收。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (77, '6816447d31bb0', '68163f929613d', '<p>下列关于模板工程施工的说法，错误的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"模板支撑体系需进行承载力验算\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"梁底模板支架立杆间距应符合设计要求\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"模板拆除应待混凝土强度达到规范要求\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"后浇带模板可与相邻结构模板同时拆除\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>后浇带模板应单独设置，待后浇带混凝土强度达标后再拆除，避免结构开裂。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (78, '6816447d31bba', '68163f929613d', '<p>根据《建设工程质量管理条例》，建设工程的最低保修期限自（ ）起计算</p>', '[{\"check\": \"A\", \"title\": \"开工日期\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"竣工验收合格之日\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"备案日期\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"交付使用之日\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>保修期从工程竣工验收合格之日开始计算，而非交付或备案时间。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (79, '6816447d31bc3', '68163f929613d', '<p>建筑工程中，用于测定砂浆强度的试件尺寸是（ ）</p>', '[{\"check\": \"A\", \"title\": \"70.7mm×70.7mm×70.7mm\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"100mm×100mm×100mm\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"150mm×150mm×150mm\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"200mm×200mm×200mm\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>砂浆强度试件为70.7mm立方体，符合《建筑砂浆基本性能试验方法标准》。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (80, '6816447d31bcc', '68163f929613d', '<p>下列属于建筑工程施工安全技术交底主要内容的是（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程概况与进度计划\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"安全操作规程与应急措施\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"工程量清单与计价方式\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工合同与违约责任\", \"is_check\": false}]', 1.00, '[\"B\"]', '<p>安全技术交底重点包括施工工艺、安全操作要求、危险点分析、应急措施等。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (81, '6816447d31bd9', '68163f929613d', '<p>根据《建筑工程施工质量验收统一标准》，分部工程验收的组织者是（ ）</p>', '[{\"check\": \"A\", \"title\": \"总监理工程师\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"专业监理工程师\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"建设单位项目负责人\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工单位技术负责人\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>分部工程由总监理工程师组织施工单位项目负责人和项目技术负责人等进行验收。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (82, '6816447d31be7', '68163f929613d', '<p>建筑工程中，下列不属于违法分包的情形是（ ）</p>', '[{\"check\": \"A\", \"title\": \"总承包单位将主体结构施工分包\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"分包单位将其承包的工程再分包\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"未经建设单位同意的专业分包\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"劳务分包单位承接劳务作业\", \"is_check\": true}]', 1.00, '[\"D\"]', '<p>劳务分包不属于违法分包，主体结构分包、再分包、未经同意的专业分包均属违法。</p>', 1, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (83, '6816447d31bf1', '68163f929613d', '<p>下列属于建筑工程施工许可证申请条件的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"已办理用地批准手续\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"已确定施工企业\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"已签订监理合同\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"施工图设计文件已审查合格\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\"]', '<p>拆迁工作只需满足施工进度要求，不要求全部完成；监理合同非施工许可必备条件（部分地区除外）。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (84, '6816447d31bfa', '68163f929613d', '<p>下列关于钢筋代换的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"应遵循“等强度代换”或“等面积代换”原则\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"代换后应满足配筋率、最小钢筋直径等构造要求\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"重要构件的钢筋代换需经设计单位同意\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"钢种代换时应考虑抗裂性、耐久性等影响\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>钢筋代换不要求强度等级必须更高，满足设计要求即可，E选项错误。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (85, '6816447d31c05', '68163f929613d', '<p>根据《建设工程安全生产管理条例》，施工单位应编制专项施工方案并组织专家论证的工程有（ ）</p>', '[{\"check\": \"A\", \"title\": \"深度5m的基坑支护工程\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"高度50m的落地式钢管脚手架工程\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"跨度36m的钢结构安装工程\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"高度8m的模板支撑工程\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\", \"E\"]', '<p>上述均属于超过一定规模的危险性较大工程，需专家论证。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (86, '6816447d31c1d', '68163f929613d', '<p>下列属于建筑工程质量验收记录的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"检验批质量验收记录\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"分项工程质量验收记录\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"分部工程质量验收记录\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"单位工程质量竣工验收记录\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工日志是施工过程记录，不属于正式质量验收记录。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (87, '6816447d31c27', '68163f929613d', '<p>下列关于混凝土施工缝处理的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"浇筑前应清除表面浮浆和松动石子\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"宜先铺一层与混凝土成分相同的水泥砂浆\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"柱的施工缝处理后应加强养护\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工缝处混凝土强度应达到1.2MPa后方可继续浇筑\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>施工缝继续浇筑时，原混凝土强度需达到1.2MPa是错误的，应根据混凝土强度增长情况和规范要求确定。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (88, '6816447d31c32', '68163f929613d', '<p>下列属于建筑工程规费的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"养老保险费\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"失业保险费\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"医疗保险费\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"住房公积金\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>规费包括“五险一金”中的四险一金（养老保险、失业保险、医疗保险、工伤保险、住房公积金），安全文明施工费属于措施费。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (89, '6816447d31c3a', '68163f929613d', '<p>根据《招标投标法》，投标人的下列行为中，属于串通投标的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"投标人之间协商投标报价\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"投标人与招标人串通抬高投标价格\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"投标人以他人名义投标\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"不同投标人的投标文件由同一单位编制\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\"]', '<p>C选项属于挂靠，E选项属于弄虚作假，均非串通投标。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (90, '6816447d31c44', '68163f929613d', '<p>下列关于建筑工程竣工验收备案的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"备案需提交工程竣工验收报告\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"备案机关为工程所在地建设行政主管部门\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"备案时间为竣工验收合格后30日内\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"备案需提交施工单位签署的工程保修书\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\", \"E\"]', '<p>备案时间应为竣工验收合格后15日内，C选项错误。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (91, '6816447d31c4f', '68163f929613d', '<p>下列属于建筑工程施工组织设计内容的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程概况与施工部署\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"施工进度计划与资源配置\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"主要施工方案与技术措施\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"施工现场平面布置图\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工组织设计不包括工程预算书，预算属于造价文件。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (92, '6816447d31c5b', '68163f929613d', '<p>下列关于建筑工程钢筋保护层厚度的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"保护层厚度不足易导致钢筋锈蚀\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"梁柱保护层厚度应符合设计规范要求\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"板类构件保护层厚度允许偏差为±5mm\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"保护层厚度可通过钢筋探测仪检测\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\", \"E\"]', '<p>板类构件保护层厚度允许偏差为±10mm（C25~C45），C选项错误。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (93, '6816447d31c66', '68163f929613d', '<p>下列属于建筑工程质量事故处理程序的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"事故调查与原因分析\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"制定处理方案\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"事故处理与验收\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"事故责任追究\", \"is_check\": false}]', 1.00, '[\"A\", \"B\", \"C\", \"E\"]', '<p>责任追究属于行政管理程序，非质量事故技术处理程序的必要步骤。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (94, '6816447d31c70', '68163f929613d', '<p>下列关于建筑工程脚手架连墙件设置的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"应靠近主节点设置，偏离不应大于300mm\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"高度24m以下可采用柔性连墙件\", \"is_check\": false}, {\"check\": \"C\", \"title\": \"高度24m以上应采用刚性连墙件\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"连墙件布置间距应符合设计计算要求\", \"is_check\": true}]', 1.00, '[\"A\", \"C\", \"D\", \"E\"]', '<p>高度24m以下脚手架可采用刚性或柔性连墙件，但柔性连墙件需满足抗风荷载要求，B选项表述不严谨。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (95, '6816447d31c7a', '68163f929613d', '<p>下列属于建筑工程施工质量控制依据的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"工程建设强制性标准\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"施工图纸与设计文件\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"施工合同约定的质量标准\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"工程质量验收规范\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工单位内部制度是管理依据，非法定质量控制依据。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (96, '6816447d31c86', '68163f929613d', '<p>下列关于建筑工程防水工程施工的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"屋面防水施工前应检查基层含水率\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"卫生间防水应做24小时闭水试验\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"卷材防水铺贴时应避免出现空鼓\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"防水混凝土浇筑时应连续施工，尽量少留施工缝\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\", \"E\"]', '<p>上述均符合防水工程施工规范要求。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (97, '6816447d31c93', '68163f929613d', '<p>下列属于建筑工程进度计划表示方法的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"横道图\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"网络图\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"直方图\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"香蕉曲线\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"D\"]', '<p>直方图和排列图用于质量控制，非进度计划表示方法。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (98, '6816447d31c98', '68163f929613d', '<p>根据《建设工程监理规范》，监理工程师的质量检查方式包括（ ）</p>', '[{\"check\": \"A\", \"title\": \"旁站\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"巡视\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"平行检验\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"见证取样\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>图纸会审是施工前准备工作，非质量检查方式。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (99, '6816447d31c9d', '68163f929613d', '<p>下列关于建筑工程装配式构件安装的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"安装前应检查构件的混凝土强度\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"构件连接部位的预埋件应清理干净\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"安装顺序应遵循“先竖向构件、后水平构件”原则\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"套筒灌浆连接应进行饱满度检测\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\", \"E\"]', '<p>上述均为装配式构件安装的基本要求。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (100, '6816447d31ca8', '68163f929613d', '<p>下列属于建筑工程安全事故等级划分依据的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"事故造成的人员伤亡数量\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"事故造成的直接经济损失\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"事故造成的社会影响程度\", \"is_check\": false}, {\"check\": \"D\", \"title\": \"事故发生的部位和性质\", \"is_check\": false}]', 1.00, '[\"A\", \"B\"]', '<p>安全事故等级主要根据伤亡人数和直接经济损失划分，社会影响等为参考因素。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (101, '6816447d31cb2', '68163f929613d', '<p>下列关于建筑工程施工图设计文件审查的说法，正确的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"审查内容包括消防、节能、结构安全性\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"审查机构需具备相应资质\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"未经审查或审查不合格的图纸不得使用\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"审查合格后需加盖审查专用章\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\"]', '<p>施工图审查属于技术性审查，非行政许可，但未通过审查不得施工。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (102, '6816447d31cbf', '68163f929613d', '<p>下列属于建筑工程施工成本构成的有（ ）</p>', '[{\"check\": \"A\", \"title\": \"人工费\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"材料费\", \"is_check\": true}, {\"check\": \"C\", \"title\": \"机械使用费\", \"is_check\": true}, {\"check\": \"D\", \"title\": \"企业管理费\", \"is_check\": true}]', 1.00, '[\"A\", \"B\", \"C\", \"D\", \"E\"]', '<p>施工成本包括直接成本（人、材、机）、间接成本（管理费）、利润和税金。</p>', 2, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (103, '6816447d31cc8', '68163f929613d', '<p>建筑工程施工中，项目经理必须取得注册建造师资格证书。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《注册建造师管理规定》明确项目经理必须由注册建造师担任。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (104, '6816447d31ccb', '68163f929613d', '<p>混凝土强度等级越高，其立方体抗压强度标准值越大。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>混凝土强度等级按立方体抗压强度标准值划分，C30＞C25，依此类推。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (105, '6816447d31ccf', '68163f929613d', '<p>建设单位可以直接指定分包工程的承包人。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>《建筑法》禁止建设单位指定分包商，分包需经总承包单位同意。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (106, '6816447d31cd2', '68163f929613d', '<p>建筑工程的保修期自工程交付使用之日起计算。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>保修期自竣工验收合格之日起计算，非交付使用日。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (107, '6816447d31cd6', '68163f929613d', '<p>钢筋混凝土结构中，受压钢筋的锚固长度应小于受拉钢筋的锚固长度。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>受压钢筋锚固长度通常为受拉锚固长度的0.7倍，因压力可辅助钢筋锚固。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (108, '6816447d31cdd', '68163f929613d', '<p>危险性较大的分部分项工程专项施工方案需经施工单位项目经理签字后实施。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>专项方案需经施工单位技术负责人和总监理工程师签字，非项目经理。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (109, '6816447d31ce0', '68163f929613d', '<p>建筑工程施工中，高处作业人员必须佩戴安全带，且高挂低用。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>高处作业安全规范要求安全带应高挂低用，防止坠落伤害。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (110, '6816447d31ce4', '68163f929613d', '<p>施工单位可以将其承包的全部建筑工程转包给其他单位。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>转包属于违法行为，《建筑法》禁止全部转包或肢解后转包。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (111, '6816447d31ce8', '68163f929613d', '<p>混凝土灌注桩施工时，桩顶标高应比设计标高至少高出0.5m。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>灌注桩需超灌一定高度以保证桩头混凝土强度，通常≥0.5m（端承桩≥1.0m）。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (112, '6816447d31ceb', '68163f929613d', '<p>建筑工程质量验收中，检验批验收不合格时，可直接进行返工处理。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>检验批不合格可返工或返修，处理后需重新验收，合格后方可进入下道工序。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (113, '6816447d31cf2', '68163f929613d', '<p>建设工程招标中，投标人报价低于成本价的，评标委员会应当否决其投标。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《招标投标法》规定，低于成本的报价视为无效，应否决。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (114, '6816447d31cfb', '68163f929613d', '<p>钢筋机械连接接头的等级分为Ⅰ级、Ⅱ级、Ⅲ级，其中Ⅰ级接头性能最优。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>Ⅰ级接头抗拉强度不小于被连接钢筋抗拉强度标准值，性能高于Ⅱ级、Ⅲ级。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (115, '6816447d31cfe', '68163f929613d', '<p>建筑工程施工组织设计中的施工方案应包括施工方法和工艺要求。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>施工方案是施工组织设计的核心，需明确施工方法、工艺、技术措施等。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (116, '6816447d31d02', '68163f929613d', '<p>建设单位在领取施工许可证后，因故不能按期开工的，应当向发证机关申请延期，延期以两次为限。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建筑法》规定，施工许可证可延期两次，每次3个月，最长可延迟9个月。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (117, '6816447d31d05', '68163f929613d', '<p>混凝土浇筑过程中，监理工程师发现模板支撑存在安全隐患，有权要求施工单位暂停施工。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>监理工程师有现场安全检查权，发现隐患可要求停工整改。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (118, '6816447d31d17', '68163f929613d', '<p>建筑工程中，烧结普通砖的强度等级用MU表示，如MU10表示抗压强度平均值≥10MPa。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>MU为砖的强度等级符号，MU10即抗压强度平均值≥10.0MPa。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (119, '6816447d31d1e', '68163f929613d', '<p>施工单位在施工中偷工减料的，责令改正，处工程合同价款2%以上4%以下的罚款。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": true}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": false}]', 1.00, '[\"A\"]', '<p>《建设工程质量管理条例》第六十四条规定了此处罚标准。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (120, '6816447d31d22', '68163f929613d', '<p>建筑工程的评标委员会成员人数为5人以上单数，其中技术、经济等方面的专家不得少于成员总数的1/3。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>专家人数不得少于成员总数的2/3，而非1/3。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (121, '6816447d31d25', '68163f929613d', '<p>钢筋混凝土梁中，纵向受力钢筋的保护层厚度是指钢筋外边缘至混凝土表面的距离。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>保护层厚度是指钢筋外边缘至混凝土表面的距离，而非钢筋中心。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
INSERT INTO `t_tenant_exam_question` (`id`, `uid`, `tenant_exam_library_uid`, `title`, `option`, `score`, `answer`, `analysis`, `exam_type`, `sort`, `is_show`, `create_time`, `update_time`, `delete_time`, `level`, `admin_id`, `tenant_id`, `chapter_uid`) VALUES (122, '6816447d31d29', '68163f929613d', '<p>建筑工程竣工验收时，单位工程质量验收合格后，建设单位即可投入使用。（ ）</p>', '[{\"check\": \"A\", \"title\": \"正确\", \"is_check\": false}, {\"check\": \"B\", \"title\": \"错误\", \"is_check\": true}]', 1.00, '[\"B\"]', '<p>验收合格后需完成备案手续，未备案的工程不得交付使用。</p>', 3, 1, 1, 1746289789, 1746289789, NULL, 1, 1, 1, '');
COMMIT;

-- ----------------------------
-- Table structure for t_tenant_file
-- ----------------------------
DROP TABLE IF EXISTS `t_tenant_file`;
CREATE TABLE `t_tenant_file` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `tenant_id` int(11) NOT NULL COMMENT '租户ID',
  `cid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '类目ID',
  `source_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '上传者id',
  `source` tinyint(1) NOT NULL DEFAULT '0' COMMENT '来源类型[0-后台,1-用户]',
  `type` tinyint(2) unsigned NOT NULL DEFAULT '10' COMMENT '类型[10=图片, 20=视频]',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '文件名称',
  `uri` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '文件路径',
  `create_time` int(10) unsigned DEFAULT NULL COMMENT '创建时间',
  `update_time` int(10) DEFAULT NULL COMMENT '更新时间',
  `delete_time` int(10) DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='文件表';

-- ----------------------------
-- Records of t_tenant_file
